function skip_accents( $str, $charset='utf-8' ) {
 
    $str = htmlentities( $str, ENT_NOQUOTES, $charset );
    
    $str = preg_replace( '#&([A-za-z])(?:acute|cedil|caron|circ|grave|orn|ring|slash|th|tilde|uml);#', '\1', $str );
    $str = preg_replace( '#&([A-za-z]{2})(?:lig);#', '\1', $str );
    $str = preg_replace( '#&[^;]+;#', '', $str );
    
    return $str;
}

//--------------------------------------------------------------------------------------------------------------------------------------//

// Opening the socket to the Rizon network
$socket = fsockopen("irc.freenode.net", 6667);

// Send auth info
fputs($socket, "PASS " . $password . "\n");
fputs($socket, "NICK " . $nickname . "\n");
fputs($socket, "USER " . $nickname . " 0 * :" . $master . "'s Bot\n");

// Join channel
foreach($channels as $channel) {
	fputs($socket, "JOIN " . $channel . "\n");
}
fputs($socket,"MODE #ETM +o $nickname \n");
// Force an endless while
$lastnick = "";
$state_quizz = false;
while (1) {
	
    // Continue the rest of the script here
    while ($data = fgets($socket, 1024)) {
        echo $data;
		
		//Get last nick
		$matchesirc = null;
		preg_match("/([a-zA-Z0-9_]*)![~A-Za-z0-9]*@([.0-9a-z\/]*)/",$data,$matchesirc);
		if(count($matchesirc)>0) {
			$lastnick = $matchesirc[1];
		}
		
        flush();
        
        // Separate all data
        $ex = explode(' ', $data);
		
		//Get all arguments (after command)
		$arg = "";
		$t_arg = $ex;
		unset($t_arg[0],$t_arg[1],$t_arg[2]);
		$arg = strtolower(implode(" ", $t_arg));
		
        $retour = explode(':',$data);
		if(rtrim($retour[0]) == 'PING')
		{
			fputs($socket,'PONG :'.$retour[1]);
		}
		if(rtrim($ex[1]) == 'JOIN')
		{
			$nick = rtrim($lastnick);
			echo "nick : ".$lastnick." \r\n";
			fputs($socket,"PRIVMSG #ETM :Bonjour $nick ! Pas trop difficile le confinement ?\n");
			if(preg_match('/pasteur*/', strtolower($nick))) {
				fputs($socket,"MODE #ETM +v $nick \n");
			}
			if($nick == "Rodolphe") {
				fputs($socket,"MODE #ETM +o $nick \n");
			}
		}
		if($ex[1] == 'KICK')
			{
					if($ex[3] == $nickname)
					{
							fputs($socket,"JOIN #ETM\r\n");
					}
			}
        
        // executes chat command
        if ($ex[0] != 'PING' && ISSET($ex[3])) {
            $command = str_replace(array(
                chr(10),
                chr(13)
            ), '', $ex[3]);
			$command = strtolower($command);
            if ($command == ":!time") {
                fputs($socket, "PRIVMSG " . $ex[2] . " :" . date(DATE_RFC2822) . " \n");
            }
            if ($command == ":!help") {
                fputs($socket, "PRIVMSG " . $ex[2] . " :\x02 \x0302,00 Robot #ETM à votre service !\x03 \x02 \n");
                fputs($socket, "PRIVMSG " . $ex[2] . " :\x02 \x0302,00 !help, !quizz, !bible, !bible livre chapitre:versetDebut-versetFin, !time\x03 \x02 \n");
            }
			if ($command == ":!quizz") {
				if(!$state_quizz) {
					$state_quizz = true;
					$nb = count($questions)-1;
					$random = rand(0,$nb);
					$newref = $reponses[$random];
					fputs($socket, "PRIVMSG " . $ex[2] . " :\x0302,00".$questions[$random]."\x03 \n");
					sleep(10);
					fputs($socket, "PRIVMSG " . $ex[2] . " :\x0302,00Plus que 20 secondes !\x03 \n");
					sleep(10);
					fputs($socket, "PRIVMSG " . $ex[2] . " :\x0302,00Plus que 10 secondes !\x03 \n");
					sleep(5);
					fputs($socket, "PRIVMSG " . $ex[2] . " :\x0302,00Plus que 5 secondes !\x03 \n");
					sleep(5);
					fputs($socket, "PRIVMSG " . $ex[2] . " :\x0302,00!bible ".$newref."\x03 \n");
					$ex_t = explode(' ', "\n PRIVMSG " . $ex[2] . " :!bible ".$newref);
					sendScripture($ex_t);
					$state_quizz = false;
				}			
			}
            if ($command == ":!bible") {
				$number_arg = count($ex);
				if($number_arg>=5) {
					sendScripture($ex);
				}
				else
				{
					$url = "https://evandis.com/wp-json/wp/v2/posts?categories=4";
					$jsondata = shell_exec('curl -s '.$url);
					$objectdata = json_decode($jsondata);
					$number = count($objectdata);
					$random = rand(0,$number);
					$data_t = "(".$objectdata[$random]->title->rendered.") ".strip_tags($objectdata[$random]->content->rendered);
					
					$datas = preg_replace('/u([\da-fA-F]{4})/', '&#x\1;', $data_t);
				
					$delete = array("\\r","\\n","\\r","\\");
					$response = html_entity_decode(str_replace($delete, '', $datas));
					fputs($socket, "PRIVMSG " . $ex[2] . " :" . $response . " \n");
				}

            }
			if(preg_match('/botbible/', $arg)) {
					fputs($socket, "PRIVMSG " . $ex[2] . " :\x02 \x0302,00 Je suis un robot développé par Rodolphe MOULIN. Ecrire la commande !help pour montrer tout ce que je peux réaliser\x03 \x02 \n");
					fputs($socket, "PRIVMSG " . $ex[2] . " :\x02 \x0302,00 En écrivant la commande '!bible' cela affiche au hazard un verset publié sur Evandis.com\x03 \x02 \n");
					fputs($socket, "PRIVMSG " . $ex[2] . " :\x02 \x0302,00 En écrivant la commande '!bible Jean 3:16' cela affiche le verset version Louis Second 21 1910 sur https://getbible.net\x03 \x02 \n");
					fputs($socket, "PRIVMSG " . $ex[2] . " :\x02 \x0302,00 Enfin la commande '!quizz' affiche une des 600 questions de https://www.bibliquest.net et affiche après 15 seondes le verset pour réponse. Le but de ces questions est d’encourager le lecteur à une mémorisation détaillée des Saintes Écritures.\x03 \x02 \n");
			}
        }
    }
	usleep(100);
}
function sendScripture($refBible) {
	global $bookfench, $booksmall, $socket;
	$ref = "";
	$number_arg = count($refBible);
	for($i=4;$i<$number_arg;$i++) {
		$ref .= $refBible[$i]; 
	}
	echo $ref;
	$ref = str_replace("&", ";", $ref);
	echo $ref;
	$ref = strtolower(skip_accents($ref));
	$newref = str_replace($bookfench, $booksmall, $ref);
	echo $newref;
	$url = "https://getbible.net/json -d version=ls1910 -d text=$newref";
	$datatext = shell_exec('curl -s '.$url);
	$datatext = rtrim($datatext, ');');
	$datatext  = ltrim($datatext, '(');
	$jsondata = json_decode($datatext)->book[0];
	if(isset($jsondata)) {
		foreach (json_decode($datatext)->book as $jsonbook){
			$data_t = " ";
			$i=0;
			foreach ($jsonbook->chapter as $json){
				if($i>=5) {
					fputs($socket, "PRIVMSG " . $refBible[2] . " :\x02 \x0304,00 5 versets maximum pour éviter de surcharger\x03 \x02 \n");
					break;
				}
				$data_t .= $json->verse." ";
				fputs($socket, "PRIVMSG " . $refBible[2] . " :" . $json->verse . " \n");
				usleep(100);
				$i++;
			}
		}	
	}
	else {
		fputs($socket, "PRIVMSG " . $refBible[2] . " :\x02 \x0304,00 La référence n'est pas trouvé. Avez-vous indiqué le bon livre ? Exemple : 1 rois 1:1 ou 1 King 1:1 ou 1k1:1\x03 \x02 \n");
		fputs($socket, "PRIVMSG " . $refBible[2] . " :\x02 \x0304,00 Ge,Ex,Le,Nu,De,Jos,Ju,Ru,1S,2S,1K,2K,1Chr,2Chr,Ezr,Ne,Est,Jb,Ps,Pr,Ec,Song,Song,Song,Is,Je,La,Ez,Da,Ho,Joel,Am,Obad,Jona,Mi,Na,Ha,Zeph,Hagg,Zech,Ma,Mt,Mak,Lk,Jn,Ac,Ro,1Co,2Co,Ga,Ep,Phl,Co,1Th,2Th,1Ti,2Ti,Tit,Phm,He,Ja,1Pe,2Pe,1Jhn,2Jhn,3Jhn,Jude,Re\x03 \x02 \n");
	}
